import {Button, Circle, Divider, Heading, HStack, Tab, TabList, Tabs, Text, VStack} from "@chakra-ui/react";
import {ChatIcon} from "@chakra-ui/icons";
import {useContext} from "react";
import {FriendContext} from "./Home";

export const SideBar = props => {
    const {friendList, setFriendList} = useContext(FriendContext);
    return (
        <VStack py={"1.4rem"}>
            <HStack justify={"space-evenly"} w={"100%"}>
                <Heading size="md">Add Friend</Heading>
                <Button>
                    <ChatIcon/>
                </Button>
            </HStack>
            <Divider/>
            <VStack as={TabList}>
                {/*<HStack as={Tab}>*/}
                {/*    <Circle bg={"blue.500"} w={"20px"} h={"20px"}/>*/}
                {/*    <Text>Will Smit</Text>*/}
                {/*</HStack>*/}
                {/*<HStack as={Tab}>*/}
                {/*    <Circle bg={"green.700"} w={"20px"} h={"20px"}/>*/}
                {/*    <Text>Will Smit</Text>*/}
                {/*</HStack>*/}
                {friendList.map((friend) =>
                    <HStack as={Tab}>
                        <Circle bg={ friend.connected ? "green.700" : "blue.500"} w={"20px"} h={"20px"}/>
                        <Text>{friend.username}</Text>
                    </HStack>
                )}
            </VStack>
        </VStack>
    );
};
